import * as React from 'react';
import { TouchableOpacity, Text, FlatList, StyleSheet } from 'react-native';

interface Props { students: ReadonlyArray<string>; }

function StudentList({ students }: Props) {
    return (
        <FlatList data={students} renderItem={_renderItem} style={styles.container} />
    );
}

function _renderItem({ item }: { item: string }) {
    return (
        <TouchableOpacity style={styles.item}>
            <Text style={styles.title}>{item}</Text>
            <Text style={styles.subtitle}>{item}</Text>
        </TouchableOpacity>
    );
}


const styles = StyleSheet.create({
    container: {
        top: 20,
        flex: 1,
    },
    item: {
        height: 60,
        borderBottomWidth: 1,
    },
    title: {
        fontSize: 30,
        left: 10
    },
    subtitle: {
        left: 10,
        fontSize: 15
    }
});
